#!/bin/bash

# Advanced script that automatically creates a room and opens both interfaces
# This version uses a simple HTTP endpoint to get room info (if we add one)
# For now, it opens both tabs and provides instructions

URL="http://localhost:8787"

# Check if server is running
if ! curl -s "$URL" > /dev/null 2>&1; then
    echo "❌ Server is not running on $URL"
    echo "   Please start the server first: npm start"
    exit 1
fi

echo "✅ Server is running"
echo "🌐 Opening RoomBrief demo..."

# Detect browser
if command -v google-chrome > /dev/null 2>&1; then
    BROWSER="google-chrome"
    NEW_TAB_FLAG="--new-tab"
elif command -v chromium-browser > /dev/null 2>&1; then
    BROWSER="chromium-browser"
    NEW_TAB_FLAG="--new-tab"
elif command -v firefox > /dev/null 2>&1; then
    BROWSER="firefox"
    NEW_TAB_FLAG="-new-tab"
elif command -v xdg-open > /dev/null 2>&1; then
    BROWSER="xdg-open"
    NEW_TAB_FLAG=""
else
    echo "❌ No browser found. Please open $URL manually"
    exit 1
fi

# Open Viewer window (first)
echo "📺 Opening Viewer window..."
if [[ -n "$NEW_TAB_FLAG" ]]; then
    $BROWSER $NEW_TAB_FLAG "$URL" > /dev/null 2>&1 &
else
    $BROWSER "$URL" > /dev/null 2>&1 &
fi

# Wait for browser to open
sleep 2

# Open Mic window (second)
echo "🎤 Opening Mic window..."
if [[ -n "$NEW_TAB_FLAG" ]]; then
    $BROWSER $NEW_TAB_FLAG "$URL" > /dev/null 2>&1 &
else
    $BROWSER "$URL" > /dev/null 2>&1 &
fi

echo ""
echo "✅ Opened 2 browser windows/tabs"
echo ""
